package gitpackage;

public class Reverse {

	public static void main(String[] args) {
	// TODO Auto-generated method stub
	 String str="Hello", str1="";
	 char ch;
	 {
	 System.out.println("Hello");
	 
		 for(int i=0;i<str.length(); i++)
		 {
		ch=	str.charAt(i);
		 str1=ch+str1;
	 }
	 System.out.println("reverse word: "+ str1);

	}
}
}



